import { useParams, Link } from "react-router-dom";

import courseModel from "../data";

export default function CourseDetails() {
    const { courseID } = useParams();

    const course = courseModel.find(({id}) => id === courseID);

    const courseDetails = ({id, name, instructor, description, enrollmentStatus, thumbnail, duration, schedule, location, prerequisites, syllabus }) => {
        return(
            <div key={id} style={{margin: "2rem 1rem"}}>
                <h1>{name}</h1>
                <p>{description}</p>
                <img alt={name}/>
                <p>Intermediate</p>
                <p>Prerequisites: {prerequisites}</p>
                <p>Course Schedule: {schedule}</p>
                <p>Instructor: {instructor}</p>
                <div>
                    <ul>
                        <details>Course Syllabus</details>
                        {syllabus.map(({week, topic, content}) => {
                            return(
                                <li key={week}>
                                    <p>week {week}</p>
                                    <p>{topic}</p>
                                    <p>{content}</p>
                                </li>
                            );
                        })}

                    </ul>
                </div>
                <p>course will be conducted {location}</p>
                <p>Schedule: {schedule}</p>
                <p>Enrollment Status: {enrollmentStatus}</p>
                <Link to="/">All Courses</Link>
            </div>
        );
    }
    return (
        <>
            {course ? 
                courseDetails(course) 
                : 
                <div>
                    <h2>Failed loading data!</h2>
                    <Link to="/">Click here to redirect to homepage</Link>
                </div>
            }
        </>
    );
};
