import { useParams, Link } from "react-router-dom";

import courseModel from "../data";

export default function CourseDetails() {
    const { courseID } = useParams();

    const course = courseModel.find(({id}) => id === courseID);
    const courseDetails = ({ name, instructor, description, enrollmentStatus, thumbnail, duration, schedule, location, prerequisites, syllabus }) => {
        <div>
            <h1>{name}</h1>
            <p>{description}</p>
            <img alt="course"/>
            <p>Intermediate</p>
            <p>Prerequisites: {prerequisites}</p>
            <p>Course Schedule: {schedule}</p>
            <p>Instructor: {instructor}</p>
            <div>
                <ul>
                    {syllabus.map(({week, topic, content}) => {
                        return(
                            <li>
                                <p>week {week}</p>
                                <p>{topic}</p>
                                <p>{content}</p>
                            </li>
                        );
                    })}

                </ul>
            </div>
            <p>course will be conducted online</p>
            <p>Schedule: Tuesdays and Thursdays, 6:00 PM - 8:00 PM</p>
            <p>Enrollment Status: Open</p>
        </div>
    }
    return courseDetails(course);
};
