import { Link } from "react-router-dom";
import { useState } from "react";

import "../App.css";

export default function CourseCard({courseModel}){
    const [searchQuery, setSearchQuery] = useState("");
    // const listAllCourse = courseModel.map(({id, name, description, duration, thumbnail}) => {
    //     console.log(thumbnail);
    //     return(
    //         <Link to={`/course/${id}`} key = {id} style={{textDecoration: "none", color: "#000"}}>
    //             <div className="card" style={{border: "1px solid black", margin: "2rem 1.5rem", padding: "0 1rem"}}>
    //                 <h1>{name}</h1>
    //                 <img src={thumbnail}/>
    //                 <p>{description}</p>
    //                 <div>
    //                     <p>Intermediate</p>
    //                     <p>{duration}</p>
    //                 </div>
    //             </div>
    //         </Link>
    //     );
    // });

    const filterCourse = courseModel.filter(({instructor, name}) => {
        const searchValue = searchQuery.toLowerCase();
        const lowerCaseInstructorName = instructor.toLowerCase();
        const lowerCaseName = name.toLowerCase();
        return lowerCaseInstructorName.includes(searchValue) || lowerCaseName.includes(searchValue);
    });

    const searchResultCourse = filterCourse.map(({id, name, description, duration, thumbnail}) => {
        return(
            <Link to={`/course/${id}`} key = {id}                 className="card__link">
                <div className="card__wrap">
                    <h1>{name}</h1>
                    <img alt={name} src={thumbnail}/>
                    <p>{description}</p>
                    <div className="card__details">
                        <p>Intermediate</p>
                        <p>{duration}</p>
                    </div>
                </div>
            </Link>
        );
    });

    return(
        <>
            <input
                className="card__input"
                type="text"
                placeholder = "Search using Course/Instructor Name"
                value = {searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />
            <div className="card">
            {searchResultCourse}
        </div>
        </>

    );
};
