import { Link } from "react-router-dom";

import courseModel from "../data";

export default function CourseCard(){

    const course = courseModel.map(({id, name, description, duration, thumbnail}) => {
        return(
            <Link to={`/course/${id}`} key = {id} style={{textDecoration: "none", color: "#000"}}>
                <div className="card" style={{border: "1px solid black", margin: "2rem 1.5rem", padding: "0 1rem"}}>
                    <h1>{name}</h1>
                    <img alt="course"/>
                    <p>{description}</p>
                    <div>
                        <p>Intermediate</p>
                        <p>{duration}</p>
                    </div>
                </div>
            </Link>
        );
    })
    return(
        <div>
            {course}
        </div>
    );
};
